mapper.update:
update user
username = #{username}, real_name = #{realName}, status = #{status}, email = #{email}, password = #{password}, phone = #{phone}, introduction = #{introduction}, register_city = #{registerCity}, update_time = #{updateTime}, ip = #{ip}, city = #{city},
where id = #{id}

mapper.select:
select * from user


        and username like concat('%', #{username}, '%')


        and status = #{status}


        and phone = #{phone}


order by create_time desc

mapper.update:
update user
ip = #{ip}, city = #{city}, register_city = #{registerCity},
where id = #{id}
